/* Notion Clone CSS: Core variables, layout, and theming */
:root {
  --sidebar-width: 260px;
  --sidebar-bg: #f7f6f3;
  --sidebar-dark-bg: #23272f;
  --sidebar-border: #e3e2df;
  --sidebar-dark-border: #2c2f36;
  --main-bg: #fff;
  --main-dark-bg: #181a1b;
  --text: #222;
  --text-dark: #f7f6f3;
  --accent: #ececec;
  --accent-dark: #23272f;
  --shadow: 0 2px 16px rgba(0,0,0,0.07);
  --radius: 10px;
  --transition: 0.2s cubic-bezier(.4,0,.2,1);
  --scrollbar: #d1d1d1;
  --scrollbar-dark: #353535;
  --page-width: 900px;
  --mobile-sidebar-width: 80vw;
}
[data-theme="dark"] {
  --sidebar-bg: var(--sidebar-dark-bg);
  --sidebar-border: var(--sidebar-dark-border);
  --main-bg: var(--main-dark-bg);
  --text: var(--text-dark);
  --accent: var(--accent-dark);
  --scrollbar: var(--scrollbar-dark);
}
html, body {
  height: 100%;
  margin: 0;
  padding: 0;
  font-family: 'Segoe UI', 'Inter', Arial, sans-serif;
  background: var(--main-bg);
  color: var(--text);
  transition: background var(--transition), color var(--transition);
}
body {
  min-height: 100vh;
  display: flex;
  flex-direction: row;
  overflow: hidden;
}
#notion-root {
  display: flex;
  width: 100vw;
  height: 100vh;
}
.notion-sidebar {
  width: var(--sidebar-width);
  background: var(--sidebar-bg);
  border-right: 1px solid var(--sidebar-border);
  box-shadow: var(--shadow);
  display: flex;
  flex-direction: column;
  transition: background var(--transition), border var(--transition);
  z-index: 2;
}
.notion-sidebar-header {
  padding: 1.2rem 1.5rem 0.7rem 1.5rem;
  font-size: 1.3rem;
  font-weight: 700;
  letter-spacing: 1px;
  color: var(--text);
  user-select: none;
}
.notion-sidebar-pages {
  flex: 1;
  overflow-y: auto;
  padding: 0.5rem 0.5rem 1.5rem 0.5rem;
}
.notion-sidebar-page {
  display: flex;
  align-items: center;
  padding: 0.5rem 1.2rem;
  border-radius: var(--radius);
  cursor: pointer;
  font-size: 1.05rem;
  color: var(--text);
  transition: background var(--transition);
  margin-bottom: 0.2rem;
  user-select: none;
}
.notion-sidebar-page.selected {
  background: var(--accent);
  font-weight: 600;
}
.notion-sidebar-page .collapse {
  margin-right: 0.7rem;
  font-size: 1.1rem;
  opacity: 0.7;
  cursor: pointer;
}
.notion-sidebar-add {
  display: flex;
  align-items: center;
  padding: 0.7rem 1.2rem;
  border: none;
  background: none;
  color: var(--text);
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  border-radius: var(--radius);
  margin: 0.5rem 1rem 1rem 1rem;
  transition: background var(--transition);
}
.notion-sidebar-add:hover {
  background: var(--accent);
}
.notion-main {
  flex: 1;
  background: var(--main-bg);
  display: flex;
  flex-direction: column;
  align-items: center;
  overflow-y: auto;
  transition: background var(--transition);
}
.notion-page {
  width: 100%;
  max-width: var(--page-width);
  margin: 2.5rem auto 0 auto;
  background: var(--main-bg);
  border-radius: var(--radius);
  box-shadow: var(--shadow);
  padding: 2.5rem 2.5rem 3rem 2.5rem;
  min-height: 70vh;
  transition: background var(--transition);
}
.notion-page-title {
  font-size: 2.2rem;
  font-weight: 700;
  margin-bottom: 1.5rem;
  outline: none;
  border: none;
  background: none;
  color: var(--text);
  width: 100%;
  padding: 0.2rem 0.5rem;
  border-radius: 6px;
  transition: background 0.15s;
}
.notion-page-title:focus {
  background: var(--accent);
}
.notion-blocks {
  margin-top: 1.2rem;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}
.notion-block {
  min-height: 1.5rem;
  padding: 0.3rem 0.5rem;
  border-radius: 6px;
  background: none;
  color: var(--text);
  font-size: 1.08rem;
  outline: none;
  border: none;
  transition: background 0.15s;
  cursor: text;
  word-break: break-word;
  position: relative;
}
.notion-block:focus {
  background: var(--accent);
}
.notion-block.dragging {
  opacity: 0.5;
  background: #b3b3b3;
}
.notion-block-toolbar {
  position: absolute;
  left: -2.2rem;
  top: 0.1rem;
  opacity: 0.7;
  display: none;
  z-index: 10;
}
.notion-block:hover .notion-block-toolbar {
  display: block;
}
.notion-block-toolbar-btn {
  background: none;
  border: none;
  color: #888;
  font-size: 1.1rem;
  cursor: pointer;
  margin-right: 0.2rem;
  border-radius: 4px;
  padding: 0.1rem 0.3rem;
  transition: background 0.15s;
}
.notion-block-toolbar-btn:hover {
  background: var(--accent);
  color: var(--text);
}
.notion-checkbox {
  margin-right: 0.7rem;
  accent-color: #6c4cff;
  width: 1.1rem;
  height: 1.1rem;
}
.notion-bullet {
  margin-right: 0.7rem;
  font-size: 1.1rem;
}
.notion-heading {
  font-weight: 700;
  margin: 0.7rem 0 0.2rem 0;
}
.notion-toolbar {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  gap: 1.2rem;
  padding: 1.2rem 2.5rem 0.5rem 2.5rem;
  width: 100%;
  max-width: var(--page-width);
}
.notion-toggle-theme {
  background: none;
  border: none;
  color: var(--text);
  font-size: 1.3rem;
  cursor: pointer;
  border-radius: 8px;
  padding: 0.3rem 1.2rem;
  transition: background 0.2s;
}
.notion-toggle-theme:hover {
  background: var(--accent);
}
/* Custom Scrollbar */
.notion-sidebar-pages, .notion-main {
  scrollbar-width: thin;
  scrollbar-color: var(--scrollbar) var(--sidebar-bg);
}
.notion-sidebar-pages::-webkit-scrollbar, .notion-main::-webkit-scrollbar {
  width: 8px;
  background: var(--sidebar-bg);
}
.notion-sidebar-pages::-webkit-scrollbar-thumb, .notion-main::-webkit-scrollbar-thumb {
  background: var(--scrollbar);
  border-radius: 6px;
}
/* Responsive */
@media (max-width: 900px) {
  :root {
    --sidebar-width: var(--mobile-sidebar-width);
    --page-width: 98vw;
  }
  .notion-page {
    padding: 1.2rem 0.5rem 2rem 0.5rem;
  }
  .notion-toolbar {
    padding: 1.2rem 0.5rem 0.5rem 0.5rem;
  }
}
@media (max-width: 600px) {
  .notion-sidebar {
    position: absolute;
    left: 0;
    top: 0;
    height: 100vh;
    z-index: 100;
    box-shadow: 2px 0 16px rgba(0,0,0,0.13);
    width: var(--mobile-sidebar-width);
    min-width: 0;
    max-width: 100vw;
    transform: translateX(-100%);
    transition: transform 0.3s cubic-bezier(.4,0,.2,1);
  }
  .notion-sidebar.open {
    transform: translateX(0);
  }
  .notion-main {
    margin-left: 0;
  }
}
