// On-Time Task Manager JS

document.addEventListener('DOMContentLoaded', () => {
    const taskForm = document.getElementById('task-form');
    const taskInput = document.getElementById('task-input');
    const taskList = document.getElementById('task-list');

    let tasks = JSON.parse(localStorage.getItem('tasks')) || [];

    function saveTasks() {
        localStorage.setItem('tasks', JSON.stringify(tasks));
    }

    function renderTasks() {
        taskList.innerHTML = '';
        if (tasks.length === 0) {
            const li = document.createElement('li');
            li.textContent = 'No tasks yet!';
            li.style.textAlign = 'center';
            li.style.color = '#888';
            taskList.appendChild(li);
            return;
        }
        tasks.forEach((task, idx) => {
            const li = document.createElement('li');
            li.className = 'task-item' + (task.completed ? ' completed' : '');
            li.setAttribute('data-index', idx);
            li.innerHTML = `
                <span>${task.text}</span>
                <div class="task-actions">
                    <button class="action-btn complete" title="Mark as complete" aria-label="Complete Task">âœ”</button>
                    <button class="action-btn delete" title="Delete task" aria-label="Delete Task">ðŸ—‘</button>
                </div>
            `;
            li.querySelector('.complete').onclick = () => toggleComplete(idx);
            li.querySelector('.delete').onclick = () => deleteTask(idx);
            taskList.appendChild(li);
        });
    }

    function addTask(text) {
        tasks.unshift({ text, completed: false });
        saveTasks();
        renderTasks();
    }

    function toggleComplete(idx) {
        tasks[idx].completed = !tasks[idx].completed;
        saveTasks();
        renderTasks();
    }

    function deleteTask(idx) {
        tasks.splice(idx, 1);
        saveTasks();
        renderTasks();
    }

    taskForm.onsubmit = (e) => {
        e.preventDefault();
        const text = taskInput.value.trim();
        if (text) {
            addTask(text);
            taskInput.value = '';
            taskInput.focus();
        }
    };

    renderTasks();
});
